  <style>
      .france {

          /* OPTIONS ========= */
          $ratio: 1;
          $theme: #10ADED;
          $hover: #F01D32;

          /* BASE ============ */
          width: $ratio * 700px;
          height: $ratio * 740px;
          font-size: $ratio * 10px;

          margin: 3em auto;
          padding: 0;
          list-style: none;
          font-family: 'Open Sans Condensed',sans-serif;
          font-weight: 700;
          text-transform: uppercase;

          /* INITIALISATION == */
          position: relative; overflow:hidden;
      a {
          color: $theme;
          text-decoration: none; letter-spacing: -.035em; white-space: nowrap;
      // transition: all .1s ease-in-out;
      &:hover, &:focus {
                    color: $hover;
                    opacity: 1 !important;
                }
      }
      li {
          position: absolute; z-index: 1;
          line-height: 70%;
          transform: translate(-50%,-100%);
      &:hover {
           z-index: 2;
       }
      // Exception -_-v
    &.v027 { // Paris
         z-index: 0;
      &:hover {
           z-index: 0;
       }
      }
      }
      @mixin fn($size:1) {
      a {
          font-size: $size * 1em;
          opacity: ((random(60)+40)/100); // 0.4 to 1
      }
      }

